/**
 * main.c
 */
uint32_t ledsequence[50];
uint32_t actsequence[50];
uint32_t red=0, green=0, yellow=0, wrong=0;
uint32_t roundindex=1;
char roundindexarray[30];

void LED_Init(void);
void SysTick_Wait(uint32_t delay);
void LED_Out(uint32_t data);
void Clock_Delay1ms(uint32_t ms);
void Switch_Init(void);
uint32_t Switch_In(void);
uint32_t Switch_In1(void);
uint32_t Switch_In2(void);
void uintToStr(uint32_t num, char* buf);
void SysTick_Init(uint32_t period);
void DAC_Init(void);
void EnableInterrupts(void);  // Enable interrupts
long StartCritical (void);    // previous I bit, disable interrupts
void EndCritical(long sr);    // restore I bit to previous value

int main(void){
  PLL_Init(Bus80MHz);
  LED_Init();
  Switch_Init();
  Timer1A_Delay_Init();
  SysTick_Init(7272);
  ST7735_InitR(INITR_REDTAB);
  EnableInterrupts();
  srand(NVIC_ST_CURRENT_R);
  uint32_t switchpressed=0;
  uint32_t k=0;
  uint32_t now=0, now1=0, now2=0, last1=0, last2=0, last=0;
  uint32_t sequencelength=0;
  //uint32_t samecount=0;
  uint32_t continued=1;
  uint32_t doitagain=1;
  uint32_t clearact=0;

while(1){
    k=0;
    uint32_t h=0;
    for( h=0; h<sequencelength; h++){
        actsequence[h]=0;
    }
    if(clearact){
        for( h=0; h<49; h++){
               actsequence[h]=0;
           }
        clearact=0;
    }
    switchpressed=0;

 ledsequence[sequencelength]=(rand()%3)+1;
 sequencelength++;

uint32_t j=0;
for(j=0; j<sequencelength; j++){
  if(ledsequence[j]==3){
      GPIO_PORTB_DATA_R |= 4; //3 is green
      green=1;
      Timer1A_Wait_10ms(100);
      GPIO_PORTB_DATA_R &= ~4;
      green=0;

  }
  else if(ledsequence[j]==1){ //1 is red
      GPIO_PORTB_DATA_R |= 64;
      red=1;
      Timer1A_Wait_10ms(100);
      GPIO_PORTB_DATA_R &= ~64;
      red=0;
  }
  else if(ledsequence[j]==2){ //2 is yellow
      GPIO_PORTB_DATA_R |= 128;
      yellow=1;
      Timer1A_Wait_10ms(100);
      GPIO_PORTB_DATA_R &= ~128;
      yellow=0;
  }
  Timer1A_Wait_10ms(100);


}

//PC4 is Red 1
//PC6 is Yellow 2
//PE4 is Green 3
while(switchpressed==0){
continued=1;
doitagain=1;

now=Switch_In();//PC4 for Switch corresponds to red led
if((last == 0) && (now==16)){
    GPIO_PORTB_DATA_R |= 64;

    last = now;
    red=1;
}
else if((last == 16) && (now==0)){
    Timer1A_Wait_10ms(1);
    now=Switch_In();//PC4 for Switch corresponds to red led
if((last == 16) && (now==0)){
    if(k<sequencelength){
        actsequence[k]=1;
        k++;
    }
    GPIO_PORTB_DATA_R &= ~64;
    last=now;
    red=0;
}
last=now;
red=0;
}

//displayScreen();


now1=Switch_In1();  //PC5 corresponds to yellow
if((last1 == 0) && (now1==32)){
    GPIO_PORTB_DATA_R |= 128;
last1 = now1;
yellow=1;
}
else if((last1 == 32) && (now1==0)){
    Timer1A_Wait_10ms(1);
    now1=Switch_In1();//PC4 for Switch corresponds to red led
if((last1 == 32) && (now1==0)){
    if(k<sequencelength){
        actsequence[k]=2;
        k++;
    }
    GPIO_PORTB_DATA_R &= ~128;
    last1=now1;
    yellow=0;
}
last1=now1;
yellow=0;
}



now2=Switch_In2(); //PE4 corresponds to green
if((last2 == 0) && (now2==16)){
last2 = now2;
green=1;
GPIO_PORTB_DATA_R |= 4; //3 is green
}
else if((last2 == 16) && (now2==0)){
    Timer1A_Wait_10ms(1);
    now2=Switch_In2();//PC4 for Switch corresponds to red led
if((last2 == 16) && (now2==0)){
    if(k<sequencelength){
        actsequence[k]=3;
        k++;
    }
    GPIO_PORTB_DATA_R &= ~4;
    last2=now2;
    green=0;
}
last2=now2;
green=0;
}
uint32_t u=0;
//uint32_t z=0;
for(u=0; u<sequencelength; u++){
 while(actsequence[u]==0){
     continued=0;
     break;
 }
 }
if(continued==1){
    uint32_t z=0;
    for(z=0; z<sequencelength; z++){
        if((actsequence[z]!=ledsequence[z])){
            wrong=1;
             GPIO_PORTF_DATA_R |= 0x02;
             Timer1A_Wait_10ms(100);
                GPIO_PORTF_DATA_R &= ~0x02;
                switchpressed=1;
               doitagain=0;
               clearact=1;
                sequencelength=0;
                wrong=0;
                roundindex=1;
                Timer1A_Wait_10ms(100);
                ST7735_OutString("Game Over!");
                Timer1A_Wait_10ms(300);
                ST7735_FillScreen(ST7735_BLACK);
                ST7735_SetCursor(0,0);
                break;
         }

    }
    if(doitagain){
    GPIO_PORTF_DATA_R |= 0x08;
    Timer1A_Wait_10ms(100);
          GPIO_PORTF_DATA_R &= ~0x08;
          //ST7735_OutString("Passed Round");
          uintToStr(roundindex,roundindexarray);
          char passed[]="Passed Round ";
          strcat(passed, roundindexarray);
          char exclaim[]="!";
          strcat(passed, exclaim);
          ST7735_OutString(passed);
          Timer1A_Wait_10ms(300);
          roundindex++;
          switchpressed=1;
          ST7735_FillScreen(ST7735_BLACK);
          ST7735_SetCursor(0,0);

    }
}

 }


}



}



void uintToStr(uint32_t num, char* buf) {
    int i = 0;
    char temp[30];
    if (num == 0) {
        buf[0] = '0';
        buf[1] = '\0';
        return;
    }
    while (num > 0) {
        temp[i++] = (num % 10) + '0';
        num /= 10;
    }
    int j=0;
    for (j = 0; j < i; j++) {
        buf[j] = temp[i - j - 1];
    }
    buf[i] = '\0';
}






